* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

body {
    font-family: 'Arial', sans-serif;
    background: linear-gradient(135deg, #0c0c2e 0%, #1a1a4a 50%, #2d1b69 100%);
    color: white;
    min-height: 100vh;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    overflow: hidden;
}

/* Animated starfield background */
body::before {
    content: '';
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background-image: 
        radial-gradient(2px 2px at 20px 30px, white, transparent),
        radial-gradient(2px 2px at 40px 70px, white, transparent),
        radial-gradient(1px 1px at 90px 40px, white, transparent),
        radial-gradient(1px 1px at 130px 80px, white, transparent),
        radial-gradient(2px 2px at 160px 30px, white, transparent);
    background-repeat: repeat;
    background-size: 200px 100px;
    animation: twinkle 3s ease-in-out infinite alternate;
    z-index: -1;
}

@keyframes twinkle {
    0% { opacity: 0.3; }
    100% { opacity: 1; }
}

.game-container {
    position: relative;
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 20px;
}

.game-header {
    text-align: center;
    margin-bottom: 10px;
}

.game-header h1 {
    font-size: 2.5em;
    margin-bottom: 10px;
    text-shadow: 0 0 20px #4a90e2;
    background: linear-gradient(45deg, #4a90e2, #7b68ee);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
}

.game-stats {
    display: flex;
    gap: 30px;
    justify-content: center;
    font-size: 1.2em;
    font-weight: bold;
}

.game-stats > div {
    padding: 8px 16px;
    background: rgba(255, 255, 255, 0.1);
    border-radius: 20px;
    border: 2px solid rgba(255, 255, 255, 0.2);
    backdrop-filter: blur(10px);
}

#gameCanvas {
    border: 3px solid #4a90e2;
    border-radius: 10px;
    background: linear-gradient(180deg, #000428 0%, #004e92 100%);
    box-shadow: 
        0 0 30px rgba(74, 144, 226, 0.5),
        inset 0 0 50px rgba(0, 0, 0, 0.3);
    cursor: none;
}

.screen {
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    background: rgba(0, 0, 0, 0.9);
    padding: 40px;
    border-radius: 15px;
    text-align: center;
    border: 2px solid #4a90e2;
    backdrop-filter: blur(10px);
    box-shadow: 0 0 30px rgba(74, 144, 226, 0.3);
    z-index: 100;
}

.screen.hidden {
    display: none;
}

.screen h2 {
    font-size: 2em;
    margin-bottom: 20px;
    color: #4a90e2;
    text-shadow: 0 0 10px #4a90e2;
}

.screen p {
    font-size: 1.2em;
    margin-bottom: 15px;
    line-height: 1.5;
}

.instructions {
    text-align: left;
    margin: 20px 0;
    padding-left: 20px;
}

.instructions li {
    margin-bottom: 8px;
    font-size: 1.1em;
}

.game-button {
    background: linear-gradient(45deg, #4a90e2, #7b68ee);
    color: white;
    border: none;
    padding: 15px 30px;
    font-size: 1.2em;
    font-weight: bold;
    border-radius: 25px;
    cursor: pointer;
    transition: all 0.3s ease;
    box-shadow: 0 4px 15px rgba(74, 144, 226, 0.4);
    margin-top: 20px;
}

.game-button:hover {
    transform: translateY(-2px);
    box-shadow: 0 6px 20px rgba(74, 144, 226, 0.6);
    background: linear-gradient(45deg, #5ba0f2, #8b78fe);
}

.game-button:active {
    transform: translateY(0);
    box-shadow: 0 2px 10px rgba(74, 144, 226, 0.4);
}

.controls-info {
    position: fixed;
    bottom: 20px;
    left: 50%;
    transform: translateX(-50%);
    background: rgba(0, 0, 0, 0.7);
    padding: 10px 20px;
    border-radius: 20px;
    font-size: 0.9em;
    border: 1px solid rgba(255, 255, 255, 0.2);
    backdrop-filter: blur(10px);
}

/* Responsive design */
@media (max-width: 900px) {
    #gameCanvas {
        width: 90vw;
        height: 60vh;
    }

    .game-header h1 {
        font-size: 2em;
    }

    .game-stats {
        flex-direction: column;
        gap: 10px;
    }

    .screen {
        width: 90vw;
        padding: 30px 20px;
    }
}

@media (max-width: 600px) {
    .game-header h1 {
        font-size: 1.5em;
    }

    .game-stats {
        font-size: 1em;
    }

    .screen h2 {
        font-size: 1.5em;
    }

    .screen p {
        font-size: 1em;
    }

    .controls-info {
        font-size: 0.8em;
        padding: 8px 15px;
    }
}

/**
 * Galaxy Defender - Space Avoidance Game
 * A vertical scrolling space game where players pilot a rocket to avoid asteroids and UFOs while collecting stars
 */

class GalaxyDefender {
    constructor() {
        // Canvas and context setup
        this.canvas = document.getElementById('gameCanvas');
        this.ctx = this.canvas.getContext('2d');

        // Game state management
        this.gameState = 'start'; // 'start', 'playing', 'paused', 'gameOver'
        this.score = 0;
        this.startTime = 0;
        this.elapsedTime = 0;
        this.level = 1;

        // Game settings
        this.baseSpeed = 2;
        this.currentSpeed = this.baseSpeed;
        this.spawnRate = 0.02;
        this.difficultyIncreaseInterval = 10000; // 10 seconds in milliseconds
        this.lastDifficultyIncrease = 0;

        // Player properties
        this.player = {
            x: this.canvas.width / 2,
            y: this.canvas.height - 80,
            width: 40,
            height: 40,
            emoji: 'üöÄ'
        };

        // Game objects arrays
        this.stars = [];
        this.asteroids = [];
        this.ufos = [];
        this.particles = [];

        // Mouse tracking
        this.mouseX = this.canvas.width / 2;

        // Animation frame ID for cleanup
        this.animationId = null;

        // Initialize the game
        this.init();
    }

    /**
     * Initialize game event listeners and UI elements
     */
    init() {
        // Mouse movement tracking
        this.canvas.addEventListener('mousemove', (e) => {
            const rect = this.canvas.getBoundingClientRect();
            this.mouseX = e.clientX - rect.left;
        });

        // Keyboard controls
        document.addEventListener('keydown', (e) => {
            switch(e.code) {
                case 'Space':
                    e.preventDefault();
                    this.togglePause();
                    break;
                case 'KeyR':
                    if (this.gameState === 'gameOver') {
                        this.restart();
                    }
                    break;
            }
        });

        // Button event listeners
        document.getElementById('startButton').addEventListener('click', () => {
            this.startGame();
        });

        document.getElementById('restartButton').addEventListener('click', () => {
            this.restart();
        });

        // Initial render
        this.render();
    }

    /**
     * Start a new game
     */
    startGame() {
        this.gameState = 'playing';
        this.score = 0;
        this.level = 1;
        this.currentSpeed = this.baseSpeed;
        this.startTime = Date.now();
        this.lastDifficultyIncrease = 0;

        // Reset player position
        this.player.x = this.canvas.width / 2;
        this.player.y = this.canvas.height - 80;

        // Clear all game objects
        this.stars = [];
        this.asteroids = [];
        this.ufos = [];
        this.particles = [];

        // Hide start screen
        document.getElementById('startScreen').classList.add('hidden');

        // Start game loop
        this.gameLoop();
    }

    /**
     * Main game loop
     */
    gameLoop() {
        if (this.gameState !== 'playing') return;

        this.update();
        this.render();

        this.animationId = requestAnimationFrame(() => this.gameLoop());
    }

    /**
     * Update game logic
     */
    update() {
        // Update elapsed time
        this.elapsedTime = Date.now() - this.startTime;

        // Update difficulty every 10 seconds
        if (this.elapsedTime - this.lastDifficultyIncrease >= this.difficultyIncreaseInterval) {
            this.increaseDifficulty();
            this.lastDifficultyIncrease = this.elapsedTime;
        }

        // Update player position (horizontal mouse following)
        const targetX = this.mouseX - this.player.width / 2;
        this.player.x = Math.max(0, Math.min(this.canvas.width - this.player.width, targetX));

        // Spawn new objects
        this.spawnObjects();

        // Update all game objects
        this.updateStars();
        this.updateAsteroids();
        this.updateUFOs();
        this.updateParticles();

        // Check collisions
        this.checkCollisions();

        // Update UI
        this.updateUI();
    }

    /**
     * Increase game difficulty
     */
    increaseDifficulty() {
        this.level++;
        this.currentSpeed += 0.5;
        this.spawnRate = Math.min(0.05, this.spawnRate + 0.005);

        // Create level up particles
        this.createLevelUpEffect();
    }

    /**
     * Spawn new game objects
     */
    spawnObjects() {
        // Spawn stars (good items)
        if (Math.random() < this.spawnRate * 0.6) {
            this.stars.push({
                x: Math.random() * (this.canvas.width - 30),
                y: -30,
                width: 30,
                height: 30,
                emoji: '‚≠ê',
                speed: this.currentSpeed * 0.8
            });
        }

        // Spawn asteroids (bad items)
        if (Math.random() < this.spawnRate * 0.7) {
            this.asteroids.push({
                x: Math.random() * (this.canvas.width - 35),
                y: -35,
                width: 35,
                height: 35,
                emoji: '‚òÑÔ∏è',
                speed: this.currentSpeed,
                rotation: Math.random() * Math.PI * 2
            });
        }

        // Spawn UFOs (bad items)
        if (Math.random() < this.spawnRate * 0.4) {
            this.ufos.push({
                x: Math.random() * (this.canvas.width - 40),
                y: -40,
                width: 40,
                height: 40,
                emoji: 'üõ∏',
                speed: this.currentSpeed * 1.2,
                wobble: 0
            });
        }
    }

    /**
     * Update star positions and remove off-screen stars
     */
    updateStars() {
        for (let i = this.stars.length - 1; i >= 0; i--) {
            const star = this.stars[i];
            star.y += star.speed;

            // Remove stars that are off-screen
            if (star.y > this.canvas.height) {
                this.stars.splice(i, 1);
            }
        }
    }

    /**
     * Update asteroid positions and remove off-screen asteroids
     */
    updateAsteroids() {
        for (let i = this.asteroids.length - 1; i >= 0; i--) {
            const asteroid = this.asteroids[i];
            asteroid.y += asteroid.speed;
            asteroid.rotation += 0.05;

            // Remove asteroids that are off-screen
            if (asteroid.y > this.canvas.height) {
                this.asteroids.splice(i, 1);
            }
        }
    }

    /**
     * Update UFO positions and remove off-screen UFOs
     */
    updateUFOs() {
        for (let i = this.ufos.length - 1; i >= 0; i--) {
            const ufo = this.ufos[i];
            ufo.y += ufo.speed;
            ufo.wobble += 0.1;
            ufo.x += Math.sin(ufo.wobble) * 0.5; // Slight horizontal wobble

            // Remove UFOs that are off-screen
            if (ufo.y > this.canvas.height) {
                this.ufos.splice(i, 1);
            }
        }
    }

    /**
     * Update particle effects
     */
    updateParticles() {
        for (let i = this.particles.length - 1; i >= 0; i--) {
            const particle = this.particles[i];
            particle.x += particle.vx;
            particle.y += particle.vy;
            particle.life -= particle.decay;

            if (particle.life <= 0) {
                this.particles.splice(i, 1);
            }
        }
    }

    /**
     * Check collisions between player and all objects
     */
    checkCollisions() {
        // Check star collisions (good)
        for (let i = this.stars.length - 1; i >= 0; i--) {
            if (this.isColliding(this.player, this.stars[i])) {
                // Collect star
                this.score += 50;
                this.createCollectionEffect(this.stars[i].x, this.stars[i].y);
                this.stars.splice(i, 1);
            }
        }

        // Check asteroid collisions (bad)
        for (let i = 0; i < this.asteroids.length; i++) {
            if (this.isColliding(this.player, this.asteroids[i])) {
                this.gameOver();
                return;
            }
        }

        // Check UFO collisions (bad)
        for (let i = 0; i < this.ufos.length; i++) {
            if (this.isColliding(this.player, this.ufos[i])) {
                this.gameOver();
                return;
            }
        }
    }

    /**
     * Check if two objects are colliding
     */
    isColliding(obj1, obj2) {
        return obj1.x < obj2.x + obj2.width &&
               obj1.x + obj1.width > obj2.x &&
               obj1.y < obj2.y + obj2.height &&
               obj1.y + obj1.height > obj2.y;
    }

    /**
     * Create particle effect for star collection
     */
    createCollectionEffect(x, y) {
        for (let i = 0; i < 8; i++) {
            this.particles.push({
                x: x + 15,
                y: y + 15,
                vx: (Math.random() - 0.5) * 6,
                vy: (Math.random() - 0.5) * 6,
                life: 1,
                decay: 0.02,
                color: '#FFD700',
                size: Math.random() * 4 + 2
            });
        }
    }

    /**
     * Create level up effect
     */
    createLevelUpEffect() {
        for (let i = 0; i < 20; i++) {
            this.particles.push({
                x: this.canvas.width / 2,
                y: this.canvas.height / 2,
                vx: (Math.random() - 0.5) * 10,
                vy: (Math.random() - 0.5) * 10,
                life: 1,
                decay: 0.01,
                color: '#4a90e2',
                size: Math.random() * 6 + 3
            });
        }
    }

    /**
     * Handle game over
     */
    gameOver() {
        this.gameState = 'gameOver';

        // Cancel animation frame
        if (this.animationId) {
            cancelAnimationFrame(this.animationId);
        }

        // Show game over screen
        document.getElementById('finalScore').textContent = this.score;
        document.getElementById('finalTime').textContent = Math.floor(this.elapsedTime / 1000);
        document.getElementById('gameOverScreen').classList.remove('hidden');
    }

    /**
     * Toggle pause state
     */
    togglePause() {
        if (this.gameState === 'playing') {
            this.gameState = 'paused';
            document.getElementById('pauseScreen').classList.remove('hidden');
            if (this.animationId) {
                cancelAnimationFrame(this.animationId);
            }
        } else if (this.gameState === 'paused') {
            this.gameState = 'playing';
            document.getElementById('pauseScreen').classList.add('hidden');
            // Adjust start time to account for pause duration
            this.startTime = Date.now() - this.elapsedTime;
            this.gameLoop();
        }
    }

    /**
     * Restart the game
     */
    restart() {
        // Hide game over screen
        document.getElementById('gameOverScreen').classList.add('hidden');

        // Reset and start new game
        this.startGame();
    }

    /**
     * Update UI elements
     */
    updateUI() {
        document.getElementById('score').textContent = this.score;
        document.getElementById('time').textContent = Math.floor(this.elapsedTime / 1000);
        document.getElementById('level').textContent = this.level;
    }

    /**
     * Render the game
     */
    render() {
        // Clear canvas
        this.ctx.clearRect(0, 0, this.canvas.width, this.canvas.height);

        // Draw background gradient
        const gradient = this.ctx.createLinearGradient(0, 0, 0, this.canvas.height);
        gradient.addColorStop(0, '#000428');
        gradient.addColorStop(1, '#004e92');
        this.ctx.fillStyle = gradient;
        this.ctx.fillRect(0, 0, this.canvas.width, this.canvas.height);

        // Draw moving stars background
        this.drawStarfield();

        if (this.gameState === 'playing' || this.gameState === 'paused') {
            // Draw game objects
            this.drawStars();
            this.drawAsteroids();
            this.drawUFOs();
            this.drawPlayer();
            this.drawParticles();
        }
    }

    /**
     * Draw animated starfield background
     */
    drawStarfield() {
        this.ctx.fillStyle = 'rgba(255, 255, 255, 0.8)';
        const time = Date.now() * 0.001;

        for (let i = 0; i < 50; i++) {
            const x = (i * 37) % this.canvas.width;
            const y = ((i * 17 + time * 20) % this.canvas.height);
            const size = (i % 3) + 1;

            this.ctx.beginPath();
            this.ctx.arc(x, y, size * 0.5, 0, Math.PI * 2);
            this.ctx.fill();
        }
    }

    /**
     * Draw player rocket
     */
    drawPlayer() {
        this.ctx.font = `${this.player.width}px Arial`;
        this.ctx.textAlign = 'center';
        this.ctx.fillText(
            this.player.emoji,
            this.player.x + this.player.width / 2,
            this.player.y + this.player.height
        );

        // Draw player glow effect
        this.ctx.shadowColor = '#4a90e2';
        this.ctx.shadowBlur = 10;
        this.ctx.fillText(
            this.player.emoji,
            this.player.x + this.player.width / 2,
            this.player.y + this.player.height
        );
        this.ctx.shadowBlur = 0;
    }

    /**
     * Draw all stars
     */
    drawStars() {
        this.ctx.font = '30px Arial';
        this.ctx.textAlign = 'center';

        this.stars.forEach(star => {
            this.ctx.shadowColor = '#FFD700';
            this.ctx.shadowBlur = 8;
            this.ctx.fillText(
                star.emoji,
                star.x + star.width / 2,
                star.y + star.height
            );
        });
        this.ctx.shadowBlur = 0;
    }

    /**
     * Draw all asteroids
     */
    drawAsteroids() {
        this.ctx.font = '35px Arial';
        this.ctx.textAlign = 'center';

        this.asteroids.forEach(asteroid => {
            this.ctx.save();
            this.ctx.translate(asteroid.x + asteroid.width / 2, asteroid.y + asteroid.height / 2);
            this.ctx.rotate(asteroid.rotation);
            this.ctx.shadowColor = '#FF4500';
            this.ctx.shadowBlur = 6;
            this.ctx.fillText(asteroid.emoji, 0, 0);
            this.ctx.restore();
        });
        this.ctx.shadowBlur = 0;
    }

    /**
     * Draw all UFOs
     */
    drawUFOs() {
        this.ctx.font = '40px Arial';
        this.ctx.textAlign = 'center';

        this.ufos.forEach(ufo => {
            this.ctx.shadowColor = '#00FF00';
            this.ctx.shadowBlur = 10;
            this.ctx.fillText(
                ufo.emoji,
                ufo.x + ufo.width / 2,
                ufo.y + ufo.height
            );
        });
        this.ctx.shadowBlur = 0;
    }

    /**
     * Draw particle effects
     */
    drawParticles() {
        this.particles.forEach(particle => {
            this.ctx.save();
            this.ctx.globalAlpha = particle.life;
            this.ctx.fillStyle = particle.color;
            this.ctx.beginPath();
            this.ctx.arc(particle.x, particle.y, particle.size, 0, Math.PI * 2);
            this.ctx.fill();
            this.ctx.restore();
        });
    }
}

// Initialize the game when the page loads and store it in window.game
window.addEventListener('load', () => {
    window.game = new GalaxyDefender();
});

// Handle window resize
window.addEventListener('resize', () => {
    // Maintain aspect ratio and responsiveness
    const canvas = document.getElementById('gameCanvas');
    if (window.innerWidth < 900) {
        canvas.style.width = '90vw';
        canvas.style.height = '60vh';
    } else {
        canvas.style.width = '800px';
        canvas.style.height = '600px';
    }
});

// Prevent context menu on right click
document.addEventListener('contextmenu', (e) => {
    e.preventDefault();
});

// Handle visibility change to pause game when tab is not active
document.addEventListener('visibilitychange', () => {
    if (document.hidden && window.game && window.game.gameState === 'playing') {
        window.game.togglePause();
    }
});
